import React, { useEffect, useState } from "react";
import "../styles/Dashboard.css";
import { FaUserCircle } from "react-icons/fa";

const Dashboard = ({ username, onLogout }) => {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState({
    todo: [],
    inprogress: [],
    completed: [],
  });

  const [newTask, setNewTask] = useState("");
  const [deadline, setDeadline] = useState("");
  const [priority, setPriority] = useState("Medium");

  // ✅ Fetch user from backend (simulate MongoDB API)
  useEffect(() => {
    // Replace with actual API call
    const fetchUser = async () => {
      // Example mock API response
      const mockUser = { name: username || "Guest" };
      setUser(mockUser);
    };
    fetchUser();
  }, [username]);

  // ✅ Add Task
  const handleAddTask = () => {
    if (!newTask.trim()) return;
    const taskObj = {
      id: Date.now(),
      title: newTask,
      deadline,
      priority,
      tags: [],
    };
    setTasks({
      ...tasks,
      todo: [...tasks.todo, taskObj],
    });
    setNewTask("");
    setDeadline("");
    setPriority("Medium");
  };

  // ✅ Edit Task
  const handleEditTask = (column, id, newTitle) => {
    setTasks({
      ...tasks,
      [column]: tasks[column].map((task) =>
        task.id === id ? { ...task, title: newTitle } : task
      ),
    });
  };

  // ✅ Delete Task
  const handleDeleteTask = (column, id) => {
    setTasks({
      ...tasks,
      [column]: tasks[column].filter((task) => task.id !== id),
    });
  };

  // ✅ Move Task Between Columns
  const handleMoveTask = (task, fromColumn, toColumn) => {
    setTasks({
      ...tasks,
      [fromColumn]: tasks[fromColumn].filter((t) => t.id !== task.id),
      [toColumn]: [...tasks[toColumn], task],
    });
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <header className="dashboard-header">
        <div className="user-info">
          <FaUserCircle className="avatar" />
          <div>
            <h2>Welcome, {user?.name} 👋</h2>
            <a href="/profile">Profile / Settings</a>
          </div>
        </div>
        <button className="logout-btn" onClick={onLogout}>
          Logout
        </button>
      </header>

      {/* Quick Add Task */}
      <div className="add-task">
        <input
          type="text"
          placeholder="Add a new task..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <input
          type="date"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option>High</option>
          <option>Medium</option>
          <option>Low</option>
        </select>
        <button onClick={handleAddTask}>Add</button>
      </div>

      {/* Task Board */}
      <div className="task-board">
        {["todo", "inprogress", "completed"].map((col) => (
          <div key={col} className="task-column">
            <h3>
              {col === "todo"
                ? "📝 To Do"
                : col === "inprogress"
                ? "⏳ In Progress"
                : "✅ Completed"}
            </h3>
            <ul>
              {tasks[col].map((task) => (
                <li key={task.id} className="task-card">
                  <div className="task-main">
                    <input
                      type="text"
                      defaultValue={task.title}
                      onBlur={(e) =>
                        handleEditTask(col, task.id, e.target.value)
                      }
                    />
                    <span className="deadline">📅 {task.deadline}</span>
                    <span className={`priority ${task.priority.toLowerCase()}`}>
                      {task.priority}
                    </span>
                  </div>
                  <div className="task-actions">
                    {col !== "todo" && (
                      <button
                        onClick={() =>
                          handleMoveTask(task, col, "todo")
                        }
                      >
                        ⬅ To Do
                      </button>
                    )}
                    {col !== "inprogress" && (
                      <button
                        onClick={() =>
                          handleMoveTask(task, col, "inprogress")
                        }
                      >
                        🔄 In Progress
                      </button>
                    )}
                    {col !== "completed" && (
                      <button
                        onClick={() =>
                          handleMoveTask(task, col, "completed")
                        }
                      >
                        ✅ Done
                      </button>
                    )}
                    <button onClick={() => handleDeleteTask(col, task.id)}>
                      ❌ Delete
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
