// Dashboard.jsx
import React, { useEffect, useMemo, useState } from "react";
import "../styles/Dashboard.css";
import { FaUserCircle, FaBell, FaSearch, FaPlus, FaSignOutAlt } from "react-icons/fa";

/*
Props:
 - username: string (used to fetch user from backend; example: {username: 'mohi'})
 - onLogout: function
Notes:
 - Replace mock fetchUser / fetchTasks with real API calls to your backend (Express + MongoDB)
 - Example backend endpoints you can implement:
    GET  /api/user/:username      => { name, avatarUrl }
    GET  /api/tasks?username=xyz  => [ { id, title, deadline, priority, tags, project, description, completed, subtasks:[] } ]
    POST /api/tasks               => create task
    PUT  /api/tasks/:id           => update
    DELETE /api/tasks/:id         => delete
*/

const todayIso = (d = new Date()) => new Date(d.getFullYear(), d.getMonth(), d.getDate()).toISOString().slice(0,10);

const priorityOrder = { High: 0, Medium: 1, Low: 2 };

const Dashboard = ({ username = "guest", onLogout = () => {} }) => {
  // Data
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]); // flat list of tasks with project, tags etc
  const [projects, setProjects] = useState([]); // derived list or fetched
  // UI states
  const [query, setQuery] = useState("");
  const [filterProject, setFilterProject] = useState("All");
  const [filterPriority, setFilterPriority] = useState("All");
  const [sortBy, setSortBy] = useState("deadline"); // deadline, priority, created
  const [showOnly, setShowOnly] = useState("all"); // all, overdue, dueToday, upcoming7
  const [expanded, setExpanded] = useState(new Set()); // expanded task ids
  const [notifications, setNotifications] = useState([]); // {id,msg,taskId,level}
  const [quickAddTitle, setQuickAddTitle] = useState("");
  const [quickAddDeadline, setQuickAddDeadline] = useState("");
  const [quickAddPriority, setQuickAddPriority] = useState("Medium");
  const [quickAddProject, setQuickAddProject] = useState("");
  const [todayStr, setTodayStr] = useState(todayIso());

  // Mock fetch user & tasks -> replace with real fetch to backend
  useEffect(() => {
    const fetchUser = async () => {
      // Example replacement:
      // const res = await fetch(`/api/user/${username}`);
      // const data = await res.json();
      const data = { name: username ? username.charAt(0).toUpperCase() + username.slice(1) : "Guest", avatarUrl: null };
      setUser(data);
    };

    const fetchTasks = async () => {
      // replace with fetch from backend
      const mock = [
        {
          id: 1,
          title: "Finish project report",
          description: "Complete executive summary + appendices",
          deadline: todayIso(), // due today
          priority: "High",
          tags: ["work", "urgent"],
          project: "Work",
          completed: false,
          createdAt: new Date().toISOString(),
          subtasks: [{ id: "1-1", title: "Write summary", done: false }],
        },
        {
          id: 2,
          title: "Grocery shopping",
          description: "Milk, eggs, fruits",
          deadline: (() => { const d = new Date(); d.setDate(d.getDate()+2); return d.toISOString().slice(0,10); })(),
          priority: "Low",
          tags: ["personal"],
          project: "Home",
          completed: false,
          createdAt: new Date().toISOString(),
          subtasks: [],
        },
        {
          id: 3,
          title: "Book flight tickets",
          description: "Summer trip planning",
          deadline: (() => { const d = new Date(); d.setDate(d.getDate()-3); return d.toISOString().slice(0,10); })(), // overdue
          priority: "Medium",
          tags: ["travel"],
          project: "Personal",
          completed: false,
          createdAt: new Date().toISOString(),
          subtasks: [],
        },
        {
          id: 4,
          title: "Pay electricity bill",
          description: "Use netbanking",
          deadline: (() => { const d = new Date(); d.setDate(d.getDate()+6); return d.toISOString().slice(0,10); })(),
          priority: "High",
          tags: ["finance"],
          project: "Home",
          completed: true,
          createdAt: new Date().toISOString(),
          subtasks: [],
        },
      ];
      setTasks(mock);
      // derive projects
      const proj = ["All", ...Array.from(new Set(mock.map(t => t.project || "General")))];
      setProjects(proj);
    };

    fetchUser();
    fetchTasks();

    // keep today's date updated near midnight (optional)
    const timer = setInterval(() => setTodayStr(todayIso()), 60_000);
    return () => clearInterval(timer);
  }, [username]);

  // Derived values
  const counts = useMemo(() => {
    const total = tasks.length;
    const completed = tasks.filter(t => t.completed).length;
    const pending = total - completed;
    return { total, completed, pending };
  }, [tasks]);

  const dueToday = useMemo(() => tasks.filter(t => t.deadline === todayStr && !t.completed), [tasks, todayStr]);
  const overdue = useMemo(() => tasks.filter(t => (t.deadline < todayStr) && !t.completed), [tasks, todayStr]);
  const upcoming7 = useMemo(() => {
    const now = new Date(todayStr);
    const end = new Date(now); end.setDate(end.getDate() + 7);
    return tasks.filter(t => {
      const dd = new Date(t.deadline);
      return dd >= now && dd <= end && !t.completed && t.deadline !== todayStr;
    });
  }, [tasks, todayStr]);

  // Notifications generation (simple): upcoming within 2 days or overdue at load
  useEffect(() => {
    const nots = [];
    tasks.forEach(t => {
      if (!t.completed) {
        const d = new Date(t.deadline);
        const diff = (d - new Date(todayStr)) / (1000*60*60*24);
        if (diff < 0) nots.push({ id: `ov-${t.id}`, msg: `Overdue: ${t.title}`, taskId: t.id, level: "overdue" });
        else if (diff <= 2) nots.push({ id: `up-${t.id}`, msg: `Due soon: ${t.title} (${t.deadline})`, taskId: t.id, level: "soon" });
      }
    });
    setNotifications(nots);
  }, [tasks, todayStr]);

  // Filtering / Searching / Sorting
  const visibleTasks = useMemo(() => {
    let list = tasks.slice();

    if (filterProject && filterProject !== "All") list = list.filter(t => t.project === filterProject);
    if (filterPriority && filterPriority !== "All") list = list.filter(t => t.priority === filterPriority);
    if (showOnly === "overdue") list = list.filter(t => (t.deadline < todayStr) && !t.completed);
    if (showOnly === "dueToday") list = list.filter(t => t.deadline === todayStr);
    if (showOnly === "upcoming7") {
      const now = new Date(todayStr);
      const end = new Date(now); end.setDate(end.getDate()+7);
      list = list.filter(t => {
        const dd = new Date(t.deadline);
        return dd >= now && dd <= end;
      });
    }
    if (query && query.trim()) {
      const q = query.toLowerCase();
      list = list.filter(t =>
        t.title.toLowerCase().includes(q) ||
        (t.description || "").toLowerCase().includes(q) ||
        (t.tags || []).join(" ").toLowerCase().includes(q)
      );
    }

    // sort
    if (sortBy === "deadline") {
      list.sort((a,b) => (a.deadline || "").localeCompare(b.deadline || ""));
    } else if (sortBy === "priority") {
      list.sort((a,b) => (priorityOrder[a.priority] - priorityOrder[b.priority]));
    } else if (sortBy === "created") {
      list.sort((a,b) => new Date(b.createdAt) - new Date(a.createdAt));
    }

    return list;
  }, [tasks, filterProject, filterPriority, sortBy, showOnly, query, todayStr]);

  // Action handlers
  const toggleComplete = (id) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
    // TODO: PUT /api/tasks/:id to persist
  };

  const deleteTask = (id) => {
    setTasks(prev => prev.filter(t => t.id !== id));
    // TODO: DELETE /api/tasks/:id
  };

  const updateTask = (id, patch) => {
    setTasks(prev => prev.map(t => t.id === id ? { ...t, ...patch } : t));
    // TODO: PUT /api/tasks/:id with patch
  };

  const addTask = () => {
    if (!quickAddTitle.trim()) return;
    const newTask = {
      id: Date.now(),
      title: quickAddTitle,
      description: "",
      deadline: quickAddDeadline || null,
      priority: quickAddPriority,
      tags: [],
      project: quickAddProject || "General",
      completed: false,
      createdAt: new Date().toISOString(),
      subtasks: []
    };
    setTasks(prev => [newTask, ...prev]);
    if (quickAddProject && !projects.includes(quickAddProject)) setProjects(prev => [...prev, quickAddProject]);
    setQuickAddTitle(""); setQuickAddDeadline(""); setQuickAddPriority("Medium"); setQuickAddProject("");
    // TODO: POST /api/tasks
  };

  const toggleExpand = (id) => {
    setExpanded(prev => {
      const copy = new Set(prev);
      if (copy.has(id)) copy.delete(id); else copy.add(id);
      return copy;
    });
  };

  const snoozeNotification = (nid, days = 1) => {
    // move task deadline forward
    const not = notifications.find(n => n.id === nid);
    if (!not) return;
    const tId = not.taskId;
    setTasks(prev => prev.map(t => {
      if (t.id !== tId) return t;
      const d = new Date(t.deadline || todayStr);
      d.setDate(d.getDate() + days);
      return { ...t, deadline: d.toISOString().slice(0,10) };
    }));
    setNotifications(prev => prev.filter(n => n.id !== nid));
  };

  const dismissNotification = (nid) => setNotifications(prev => prev.filter(n => n.id !== nid));

  // small stats: progress %
  const progressPercent = counts.total ? Math.round((counts.completed / counts.total) * 100) : 0;

  return (
    <div className="dash-wrap">
      {/* HEADER */}
      <header className="dash-header">
        <div className="left">
          <div className="avatar-block">
            {user?.avatarUrl ? <img src={user.avatarUrl} alt="avatar" className="avatar-img" /> : <FaUserCircle className="avatar-lg" />}
            <div className="welcome">
              <div className="greeting">Welcome, <strong>{user?.name || "Guest"}</strong>!</div>
              <a className="profile-link" href="/profile">Profile & settings</a>
            </div>
          </div>
        </div>

        <div className="right">
          <div className="search">
            <FaSearch className="icon" />
            <input aria-label="Search tasks" placeholder="Search tasks, tags, projects..." value={query} onChange={(e)=>setQuery(e.target.value)} />
          </div>
          <button className="logout" onClick={onLogout} title="Logout"><FaSignOutAlt /></button>
          <div className="notif">
            <button className="notif-btn" title="Notifications"><FaBell /></button>
            <div className="notif-bubble">{notifications.length}</div>
            <div className="notif-panel">
              {notifications.length === 0 && <div className="notif-empty">No notifications</div>}
              {notifications.map(n => (
                <div key={n.id} className={`notif-item ${n.level}`}>
                  <div className="notif-msg">{n.msg}</div>
                  <div className="notif-actions">
                    <button onClick={() => snoozeNotification(n.id, 1)}>Snooze 1d</button>
                    <button onClick={() => dismissNotification(n.id)}>Dismiss</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </header>

      <main className="dash-main">
        {/* SIDEBAR: Projects / Filters */}
        <aside className="dash-sidebar">
         <div className="quick-add card">
  <div className="qa-head"><FaPlus /> Quick Add</div>
  <input
    placeholder="Task title"
    value={quickAddTitle}
    onChange={(e)=>setQuickAddTitle(e.target.value)}
  />
  <div className="row">
    <input
      type="date"
      value={quickAddDeadline}
      onChange={(e)=>setQuickAddDeadline(e.target.value)}
    />
    <select value={quickAddPriority} onChange={(e)=>setQuickAddPriority(e.target.value)}>
      <option>High</option>
      <option>Medium</option>
      <option>Low</option>
    </select>
  </div>
  <input
    placeholder="Project (optional)"
    value={quickAddProject}
    onChange={(e)=>setQuickAddProject(e.target.value)}
  />
  <button type="button" className="primary" onClick={addTask}>Add Task</button>
</div>


          <div className="card projects">
            <h4>Projects</h4>
            <ul>
              {projects.map(p => (
                <li key={p}><button className={filterProject===p ? "active" : ""} onClick={()=>setFilterProject(p)}>{p}</button></li>
              ))}
            </ul>
          </div>

          <div className="card filters">
            <h4>Filters</h4>
            <div className="filter-row">
              <label>Priority</label>
              <select value={filterPriority} onChange={e=>setFilterPriority(e.target.value)}>
                <option>All</option><option>High</option><option>Medium</option><option>Low</option>
              </select>
            </div>
            <div className="filter-row">
              <label>Show</label>
              <select value={showOnly} onChange={e=>setShowOnly(e.target.value)}>
                <option value="all">All</option>
                <option value="dueToday">Due Today</option>
                <option value="upcoming7">Upcoming 7 days</option>
                <option value="overdue">Overdue</option>
              </select>
            </div>
            <div className="filter-row">
              <label>Sort by</label>
              <select value={sortBy} onChange={e=>setSortBy(e.target.value)}>
                <option value="deadline">Deadline</option>
                <option value="priority">Priority</option>
                <option value="created">Created</option>
              </select>
            </div>
          </div>

          <div className="card stats">
            <h4>Overview</h4>
            <div className="stat-row">
              <div>Total</div><div>{counts.total}</div>
            </div>
            <div className="stat-row">
              <div>Completed</div><div>{counts.completed}</div>
            </div>
            <div className="stat-row">
              <div>Pending</div><div>{counts.pending}</div>
            </div>

            <div className="progress-wrap">
              <div className="progress-label">Progress: {progressPercent}%</div>
              <div className="progress-bar"><div style={{width: `${progressPercent}%`}}/></div>
            </div>

            <div className="mini-overview">
              <div>Due Today: <strong>{dueToday.length}</strong></div>
              <div>Next 7 days: <strong>{upcoming7.length}</strong></div>
              <div className="overdue">Overdue: <strong>{overdue.length}</strong></div>
            </div>
          </div>
        </aside>

        {/* CONTENT */}
        <section className="dash-content">
          {/* Top overview cards */}
          <div className="overview-cards">
            <div className="card small">
              <div className="card-title">Due Today</div>
              <div className="card-value">{dueToday.length}</div>
              <div className="card-list">
                {dueToday.slice(0,3).map(t=> <div key={t.id} className="mini">{t.title} <span className="prio">{t.priority}</span></div>)}
              </div>
            </div>

            <div className="card small">
              <div className="card-title">Next 7 days</div>
              <div className="card-value">{upcoming7.length}</div>
              <div className="card-list">
                {upcoming7.slice(0,3).map(t=> <div key={t.id} className="mini">{t.title} <span className="prio">{t.priority}</span></div>)}
              </div>
            </div>

            <div className="card small overdue-card">
              <div className="card-title">Overdue</div>
              <div className="card-value">{overdue.length}</div>
              <div className="card-list">
                {overdue.slice(0,3).map(t=> <div key={t.id} className="mini">{t.title} <span className="prio">{t.priority}</span></div>)}
              </div>
            </div>
          </div>

          {/* Task list */}
          <div className="card task-list">
            <div className="task-list-header">
              <h3>Tasks</h3>
              <div className="header-actions">
                <label>View:
                  <select value={filterProject==='All' ? "All projects" : filterProject} onChange={e=>setFilterProject(e.target.value)}>
                    {projects.map(p => <option key={p} value={p}>{p}</option>)}
                  </select>
                </label>
              </div>
            </div>

            <div className="list-rows">
              {visibleTasks.length === 0 && <div className="empty">No tasks found</div>}
              {visibleTasks.map(task => {
                const isExpanded = expanded.has(task.id);
                const isOverdue = task.deadline && task.deadline < todayStr && !task.completed;
                return (
                  <div key={task.id} className={`task-row ${task.completed ? "done" : ""} ${isOverdue ? "overdue" : ""}`}>
                    <div className="left-col">
                      <input type="checkbox" checked={task.completed} onChange={()=>toggleComplete(task.id)} aria-label={`Mark ${task.title} complete`} />
                    </div>

                    <div className="main-col">
                      <div className="title-row">
                        <div className="title">{task.title}</div>
                        <div className="meta">
                          {task.deadline && <span className="deadline">📅 {task.deadline}</span>}
                          <span className={`chip priority ${task.priority.toLowerCase()}`}>{task.priority}</span>
                          {task.tags && task.tags.map(tag => <span key={tag} className="tag">{tag}</span>)}
                          <span className="project">{task.project}</span>
                        </div>
                      </div>

                      {isExpanded && (
                        <div className="expanded">
                          <div className="desc">{task.description || "No additional details."}</div>
                          {task.subtasks && task.subtasks.length > 0 && (
                            <div className="subtasks">
                              <div className="subhead">Subtasks</div>
                              {task.subtasks.map(s => (
                                <div key={s.id} className="sub">
                                  <input type="checkbox" checked={s.done} readOnly /> {s.title}
                                </div>
                              ))}
                            </div>
                          )}
                          <div className="edit-actions">
                            <button onClick={()=>{ const newTitle = prompt("Edit title", task.title); if(newTitle) updateTask(task.id, { title: newTitle }); }}>Edit</button>
                            <button onClick={()=>{ const nd = prompt("Edit deadline (YYYY-MM-DD)", task.deadline || ""); if(nd) updateTask(task.id, { deadline: nd }); }}>Edit deadline</button>
                            <button onClick={()=>deleteTask(task.id)}>Delete</button>
                          </div>
                        </div>
                      )}
                    </div>

                    <div className="right-col">
                      <button onClick={()=>toggleExpand(task.id)} className="expand-btn">{isExpanded ? "Collapse" : "Details"}</button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Optional calendar / productivity */}
          <div className="bottom-row">
            <div className="card calendar">
              <h4>Calendar (preview)</h4>
              <div className="calendar-placeholder">[ Calendar widget goes here — integrate FullCalendar or react-calendar ]</div>
            </div>

            <div className="card productivity">
              <h4>Productivity</h4>
              <div className="stat">Today progress: <strong>{progressPercent}%</strong></div>
              <div className="streak">Streak: <strong>3 days</strong></div>
              <div className="mini-chart">
                <div className="bar" style={{height: "40px"}}>Completed</div>
                <div className="bar empty" style={{height: "60px"}}>Pending</div>
              </div>
            </div>
          </div>

        </section>
      </main>
    </div>
  );
};

export default Dashboard;
